(function validate(inputs) {
    const {$row, $name, $field} = inputs;
    let result = { valid: true, message: 'Ok' };
    const arrayFormatter = arr => `\n${arr.map(entry => `- "${entry}"`).join('\n')}`;
    const listFormatter = arr => `${arr.map(entry => `"${entry}"`).join(', ')}`;


   /* Contingent on the naming system for tumour staging systems to remain consistent */
    const stagingName = $name
      .trim()
      .toLowerCase()
      .split('_tumour_staging_system')[0];
    const requiredFields = [
      `${stagingName}_m_category`,
      `${stagingName}_n_category`,
      `${stagingName}_t_category`,
    ];
    const convertedRow = Object.fromEntries(
      Object.entries($row).map(([fieldName, fieldVal]) => [fieldName.toLowerCase(), fieldVal]),
    );
    /* Check for contigous spaces wrapped with quotes (empty strings) */
    const checkforEmpty = entry => {
      return /^\s+$/g.test(decodeURI(entry).replace(/^"(.*)"$/, '$1'));
    };

    /* search for fields with falsy values*/
    const emptyFields = requiredFields.filter(
      field => !convertedRow[field] || checkforEmpty(convertedRow[field]),
    );

    /* The staging system should be set to an AJCC option if the TNM fields are submitted */
    if (!($field) && emptyFields.length != requiredFields.length) {
       const errorFields = requiredFields.filter(fieldName => !emptyFields.includes(fieldName));
       result = {
         valid: false,
         message:`The ${stagingName}_tumour_staging_system field must be set to an AJCC option when the following fields are submitted: ${listFormatter(
            errorFields,
          )}`,
       };
    }
    /* This is not a required field, so first ensure that it exists */
    else if ($field) {
      /* The fields should be provided IF and ONLY IF the AJCC regex passes */
      if (/^(AJCC)\b/i.test($field) && emptyFields.length) {
        result = {
          valid: false,
          message: `The following fields are required when ${$name} is set to an AJCC option: ${arrayFormatter(
            emptyFields,
          )}`,
        };
      } else if (!/^(AJCC)\b/i.test($field) && emptyFields.length != requiredFields.length) {
        const errorFields = requiredFields.filter(fieldName => !emptyFields.includes(fieldName));
        result = {
          valid: false,
          message: `The following fields cannot be provided when ${$name} is not set to an AJCC option: ${arrayFormatter(
            errorFields,
          )}`,
        };
      }
    }
    return result;
  })

(function validate(inputs) {
    const {$row, $name, $field} = inputs;
    let result = { valid: true, message: 'Ok' };
    const stagingSystems = ['ajcc 7th edition', 'ajcc 8th edition'];
    const stagingName = $name.trim().toLowerCase().split('_m_category')[0];
    const tumourStagingSystem = `${stagingName}_tumour_staging_system`;

    /* Check for contigous spaces wrapped with quotes (empty strings) */
    const checkforEmpty = entry => {
      return /^\s+$/g.test(decodeURI(entry).replace(/^"(.*)"$/, '$1'));
    };


    if ($row[tumourStagingSystem] && $row[tumourStagingSystem] != null && !(checkforEmpty($row[tumourStagingSystem]))) {
      if (stagingSystems.includes($row[tumourStagingSystem].trim().toLowerCase())) {
         if ($field && $field != null && !(checkforEmpty($field)) && $field.trim().toLowerCase() === 'mx') {
           result = {
             valid: false,
             message: `The designation of '${$field}' in the '${$name}' field is not a valid M category in the ${tumourStagingSystem} '${$row[tumourStagingSystem]}'.`
           };
         }
      }
    }
    if ($row[tumourStagingSystem] && $row[tumourStagingSystem] != null && !(checkforEmpty($row[tumourStagingSystem]))) {
      if ($row[tumourStagingSystem].trim().toLowerCase() == "ajcc 6th edition") {
         if ($field && $field != null && !(checkforEmpty($field)) && $field.trim().toLowerCase() === 'not applicable') {
           result = {
             valid: false,
             message: `The designation of '${$field}' in the '${$name}' field is not a valid M category in the ${tumourStagingSystem} '${$row[tumourStagingSystem]}'.`
           };
         }
      }
    }
    return result;
})

