(function validate(inputs) {
    const {$row, $name, $field} = inputs;
    let result = { valid: true, message: 'Ok' };
    if ($row.tumour_grading_system && $field) {
      let codeList = [];
      const tieredGradingList = ['gx','g1','g2','g3'];
      const gradingSystems = ['two-tier grading system', 'three-tier grading system', 'four-tier grading system', 'grading system for gists', 'grading system for gnets', 'isup grading system', 'who grading system for cns tumours', 'fnclcc grading system', 'gleason grade group system', 'scarff-bloom-richardson grading system', 'nuclear grading system for dcis'];
      switch ($row.tumour_grading_system && $row.tumour_grading_system.trim().toLowerCase()) {
        case 'two-tier grading system':
          codeList = [
            'low grade',
            'high grade',
          ];
          break;
        case 'three-tier grading system':
          codeList = tieredGradingList;
          break;
        case 'four-tier grading system':
          codeList = [
            'gx',
            'g1',
            'g2',
            'g3',
            'g4',
          ];
          break;
        case 'grading system for gists':
          codeList = [
            'low',
            'high',
          ];
          break;
        case 'grading system for gnets':
          codeList = tieredGradingList;
          break;
        case 'isup grading system':
          codeList = [
            'gx',
            'g1',
            'g2',
            'g3',
            'g4',
          ];
          break;
        case 'who grading system for cns tumours':
          codeList = [
            'grade i',
            'grade ii',
            'grade iii',
            'grade iv',
          ];
          break;
        case 'fnclcc grading system':
          codeList = tieredGradingList;
          break;
        case 'gleason grade group system':
          codeList = [
            'grade group 1',
            'grade group 2',
            'grade group 3',
            'grade group 4',
            'grade group 5',
          ];
          break;
        case 'scarff-bloom-richardson grading system':
          codeList = tieredGradingList;
          break;
        case 'nuclear grading system for dcis':
          codeList = tieredGradingList;
          break;
        case 'unknown':
          codeList = ['unknown'];
          break;
        case 'not applicable':
          codeList = ['not applicable'];
          break;
      }

      if (!codeList.includes($field.trim().toLowerCase())) {
        const msg = `'${$field}' is not a permissible value. When 'tumour_grading_system' is set to '${
          $row.tumour_grading_system
        }', 'tumour_grade' must be one of the following: \n${codeList
          .map(code => `- "${code}"`)
          .join('\n')}`;
        result.valid = false;
        result.message = msg;
      }
      else if (!gradingSystems.includes($row.tumour_grading_system.trim().toLowerCase())) {
         result.valid = false;
         const msg = `'${$row.tumour_grading_system}' is not a permissible value for 'tumour_grading_system'. If the tumour grading system you use is missing, please contact the DCC.`;
         result.message = msg;
      }
    }
    return result;
  })

(function validate(inputs) {
    // When $name is percent_tumour_cells, and $field is a number between 0 and 1
    const {$row, $name, $field} = inputs;

    const result = { valid: true, message: 'Ok' };
    const measurementMethodExceptionTypes = ['not applicable'];

    // checks for a string just consisting of whitespace
    const checkforEmpty = (entry) => {
      return /^\s+$/g.test(decodeURI(entry).replace(/^"(.*)"$/, '$1'));
    };

    const fieldHasValue = $field && $field != null && !(checkforEmpty($field));
    const measurementMethod = $row?.percent_tumour_cells_measurement_method?.trim?.().toLowerCase();

    if (fieldHasValue) {
      if (measurementMethodExceptionTypes.includes(measurementMethod)) {
        return {
          valid: false,
          message: `The '${$name}' field cannot be submitted when 'percent_tumour_cells_measurement_method' = 'Not applicable'`
        };
      }
    }

    return result;
  })

(function validate(inputs) {
      const {$row, $name, $field} = inputs;
      let result = {valid: true, message: "Ok"};

      const notExamined = ['cannot be determined', 'no', 'no lymph nodes found in resected specimen', 'not applicable', 'unknown'];
      /* checks for a string just consisting of whitespace */
      const checkforEmpty = (entry) => {return /^\s+$/g.test(decodeURI(entry).replace(/^"(.*)"$/, '$1'))};

      if (!$row.lymph_nodes_examined_status || $row.lymph_nodes_examined_status === null || checkforEmpty($row.lymph_nodes_examined_status)) {
        result = {valid: false, message: `The 'lymph_nodes_examined_status' field must be submitted.`};
      }
      else {
        const lymphNodesExaminedStatus = $row.lymph_nodes_examined_status.trim().toLowerCase();

        if (!$field || $field === null || checkforEmpty($field)) {
          if (lymphNodesExaminedStatus === 'yes') {
            result = { valid: false, message: `The '${$name}' field must be submitted if the 'lymph_nodes_examined_status' field is 'Yes'`};
          }
        }
        else {
          if (notExamined.includes(lymphNodesExaminedStatus)) {
            result = { valid: false, message: `The '${$name}' field should not be submitted if the 'lymph_nodes_examined_status' field is '${lymphNodesExaminedStatus}'`};
          }
        }
      }
    return result;
})

(function validate(inputs) {
      const {$row, $name, $field} = inputs;
      let result = {valid: true, message: "Ok"};

      /* checks for a string just consisting of whitespacei */
      const checkforEmpty = (entry) => {return /^\s+$/g.test(decodeURI(entry).replace(/^"(.*)"$/, '$1'))};

      const numberLymphNodesExamined = $row.number_lymph_nodes_examined;
      const numberLymphNodesPositive = $row.number_lymph_nodes_positive;

      if ($row.lymph_nodes_examined_status != null) {
         const lymphNodesExaminedStatus = $row.lymph_nodes_examined_status.trim().toLowerCase();
         /* if lymph nodes were examined and number_lymph_nodes_examined is submitted, it must be a value greater than 0. Otherwise, this field should be 0 or left blank. */
         if (($name === "number_lymph_nodes_examined") && (!(checkforEmpty($field)))) {
            if (lymphNodesExaminedStatus === "yes") { 
               if (parseInt($field) <= 0) {
                  result = {
                    valid: false,
                    message: `The '${$name}' field must be a value greater than 0 if 'lymph_nodes_examined_status' is '${lymphNodesExaminedStatus}'`
                  };
               }
            }
            else if (parseInt($field) > 0) {
               result = {
                 valid: false,
                 message: `The '${$name}' field must be submitted as 0 or left blank if 'lymph_nodes_examined_status' is '${lymphNodesExaminedStatus}'`
               };
            }
         }

         /* If lymph nodes were examined, number_lymph_nodes_positive must be submitted and it must be a value less than or equal to number_lymph_nodes_examined (if it is submitted). Otherwise, this field should not be submitted.*/
         else if ($name === "number_lymph_nodes_positive") {
            if (lymphNodesExaminedStatus === "yes") {
               if (checkforEmpty($field) || $field == null) {
                  result = {
                     valid: false,
                     message: `The '${$name}' field must be submitted if 'lymph_nodes_examined_status' is '${lymphNodesExaminedStatus}'`
                  };
               }
               else if (parseInt($field) < 0) {
                  result = {
                    valid: false,
                    message: `The '${$name}' field must be a value greater than or equal to 0 if 'lymph_nodes_examined_status' is '${lymphNodesExaminedStatus}'`
                  };
               }
               else if ((!(checkforEmpty(numberLymphNodesExamined)) && ((parseInt($field) > parseInt(numberLymphNodesExamined))))) {
                  result = {
                    valid: false,
                    message: `The '${$name}' field must be a value less than or equal to 'number_lymph_nodes_examined' if 'lymph_nodes_examined_status' is '${lymphNodesExaminedStatus}'`
                  };
               }
            }
            else if ($field || $field != null) {
               result = {
                 valid: false,
                 message: `The '${$name}' field should not be submitted if 'lymph_nodes_examined_status' is '${lymphNodesExaminedStatus}'`
               };
            }
        }
     }
     return result;
})

